import { useState } from "react";

const useTabs = (initialIndex, allTabs) => {
  const [currentIndex, setCurrentIndex] = useState(initialIndex);

  return {
    currentItem: allTabs[currentIndex],
  };
};

export function App() {
  const tabs = [
    {
      tab: "Section 1",
      content: "This is Section 1",
    },
    {
      tab: "Section 2",
      content: "This is Section 2",
    },
  ];
  const { currentItem } = useTabs(0, tabs);

  return (
    <div>
      <h1>make the Hooks</h1>
      {tabs.map((item, index) => (
        <button key={item.tab} onClick={()=>changeItem(index)}>{item.tab}</button>
      ))}
      <div>{currentItem.content}</div>
    </div>
  );
}
