import { useState } from "react";
import { useTabs } from './hooks/useTabs';

export function App() {
  const tabs = [
    {
      tab: "Section 1",
      content: "This is Section 1",
    },
    {
      tab: "Section 2",
      content: "This is Section 2",
    },
  ];
  const { currentItem, changeItem } = useTabs(0, tabs);

  return (
    <div>
      <h1>make the Hooks</h1>
      {tabs.map((item, index) => (
        <button key={item.tab} onClick={() => changeItem(index)}>
          {item.tab}
        </button>
      ))}
      <div>{currentItem.content}</div>
    </div>
  );
}
