export const usePreventLeave = () => {
  const Listener = (e) => {
    e.preventDefault();
  };
  const protect = () => {
    window.addEventListener("beforeunload", Listener);
  };
  const unprotect = () => {
    window.removeEventListener("beforeunload", Listener);
  };

  return { protect, unprotect };
};
