import { useState } from "react";



export function App() {
  const maxLen = (value) => value.length < 5;
  const inputHook = useInput("in", maxLen);

  console.log(inputHook);

  return (
    <div>
      <input
        type="text"
        placeholder="input text"
        value={inputHook.value}
        onChange={inputHook.onChange}
      />
    </div>
  );
}
